import axios from 'axios';
import React, { Component } from 'react'

class Post extends Component {
    state = {
        post: null
    }
    componentDidMount() {
        let id = this.props.match.params.post_id;
        axios.get('https://jsonplaceholder.typicode.com/posts' + id)
            .then(res => {
                this.setState({
                    post: res.data
                })
            })
    }
    render() {
        return (
            <div className="container">
                <h4>{this.state.id}</h4>
            </div>
        )
    }
}

export default Post;
